INSERT INTO `news` VALUES ('19', '关于启用教育网域名的通知 ', 'Sun Dec 22 17:34:17 CST 2019', 'assets/images/blog/0.PNG', '校园动态', '通知', '<p style=\";text-align:justify;line-height:150%;text-indent:40px;-ms-text-autospace:ideograph-numeric;-ms-text-justify:inter-ideograph\">\r\n    <span style=\"line-height:150%;letter-spacing:2px;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">为了充分整合利用网络教育资源，为师生提供更好的学习和科研网络服务，本学期，我校正式将接入中国教育和科研计算机网。</span></span> \r\n</p>\r\n<p style=\";text-align:justify;line-height:150%;text-indent:40px;-ms-text-autospace:ideograph-numeric;-ms-text-justify:inter-ideograph\">\r\n    <span style=\"line-height:150%;letter-spacing:2px;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">即日起，我校正式启用教育网域名：</span>http://www.gdust.edu.cn，原域名：http://www.gdust.cn 一段时间内将继续使用，同时教育网域名同步支持IPV6网络访问，特此通知。</span> \r\n</p>\r\n<p style=\"background:#FFFFFF;padding:0;text-align:justify;line-height:150%;text-indent:34px;-ms-text-autospace:ideograph-numeric;-ms-text-justify:inter-ideograph\">\r\n    <span style=\"color:#333333;letter-spacing:1px;font-family:宋体;font-size:16px;font-style:normal;font-weight:normal\">  </span> \r\n</p>\r\n<p style=\";text-align:justify;line-height:150%;text-indent:420px;-ms-text-autospace:ideograph-numeric;-ms-text-justify:inter-ideograph\">\r\n    <span style=\"line-height:150%;letter-spacing:2px;font-family:宋体;font-size:16px\"><span style=\"font-family:宋体\">                               广东科技学院</span></span> \r\n</p>\r\n<p style=\";text-align:justify;line-height:150%;text-indent:400px;-ms-text-autospace:ideograph-numeric;-ms-text-justify:inter-ideograph\">\r\n    <span style=\"line-height:150%;letter-spacing:2px;font-family:宋体;font-size:16px\">                                2019年11月1日</span> \r\n</p>\r\n<p>\r\n    <br/>\r\n</p>', 'White');

